import{_ as a,c as t,o as i,a2 as n}from"./chunks/framework.ucIUlO5R.js";const u=JSON.parse('{"title":"InnerSight के बारे में","description":"","frontmatter":{},"headers":[],"relativePath":"hi/about/index.md","filePath":"hi/about/index.md","lastUpdated":1761341529000}'),l={name:"hi/about/index.md"};function r(o,e,d,h,s,p){return i(),t("div",null,[...e[0]||(e[0]=[n('<h1 id="innersight-के-बारे-में" tabindex="-1">InnerSight के बारे में <a class="header-anchor" href="#innersight-के-बारे-में" aria-label="Permalink to &quot;InnerSight के बारे में&quot;">​</a></h1><p>InnerSight, InnerSight Foundation की एक पहल है, जो मानसिक स्वास्थ्य, भावनात्मक विनियमन की प्रक्रियाएँ —जिनमें वे भी शामिल हैं जिन्हें अपरंपरागत माना जाता है— और द चर्च ऑफ़ जीसस क्राइस्ट ऑफ़ लैटर-डे सेंट्स के संदर्भ में आध्यात्मिक जीवन के अंतर्संबंध का अन्वेषण करती है।</p><h2 id="हमारा-मिशन" tabindex="-1">हमारा मिशन <a class="header-anchor" href="#हमारा-मिशन" aria-label="Permalink to &quot;हमारा मिशन&quot;">​</a></h2><p>समकालीन नैदानिक दृष्टिकोणों और कलीसियाई नेतृत्व के सिद्धांतों के बीच सम्मानजनक, सूचित और करुणामय संवाद को बढ़ावा देना ताकि:</p><ul><li>उन लोगों के प्रति कलंक कम किया जा सके जो विशिष्ट भावनात्मक या मानसिक आवश्यकताओं का अनुभव करते हैं</li><li>परमेश्वर के सभी पुत्रों और पुत्रियों के लिए पूर्ण समावेशन और अपनत्व की भावना को प्रोत्साहित किया जा सके</li><li>ऐसा मार्गदर्शन प्रदान किया जा सके जो व्यक्तिगत कल्याण और सामुदायिक सद्भाव के बीच संतुलन स्थापित करे</li><li>कलीसिया को मसीह जैसे प्रेम के शरणस्थल के रूप में सुदृढ़ किया जा सके</li></ul><h2 id="हमारा-दृष्टिकोण" tabindex="-1">हमारा दृष्टिकोण <a class="header-anchor" href="#हमारा-दृष्टिकोण" aria-label="Permalink to &quot;हमारा दृष्टिकोण&quot;">​</a></h2><p>यह पहल एक वास्तविक आवश्यकता के अवलोकन से उत्पन्न हुई: उन सदस्यों के लिए संस्थागत समझ और संरचित समर्थन की कमी जो अपने उपचार के मार्ग में भावनात्मक विनियमन के गैर-पारंपरिक तरीकों का उपयोग करते हैं। हमारा कार्य निम्न पर आधारित है:</p><ul><li>नैदानिक मनोविज्ञान के वैज्ञानिक रूप से मान्य सिद्धांत</li><li>संज्ञानात्मक-व्यवहारात्मक थेरेपी जैसे उपचारात्मक दृष्टिकोण</li><li>सहानुभूति, परोपकार और मानवीय गरिमा पर केंद्रित प्रकट सिद्धांत और ईसाई मूल्य</li></ul><p>हम मानते हैं कि आस्था और विज्ञान परस्पर विरोधी नहीं हैं, बल्कि वे एक साथ चलकर चंगाई, पारस्परिक समझ और आध्यात्मिक वृद्धि के मार्गों को प्रकाशित कर सकते हैं।</p><blockquote><p>“दया मसीह का शुद्ध प्रेम है, और वह सदैव बना रहता है; और जो कोई अंतिम दिन में इसे धारण करता पाया जाएगा, उसका भला होगा।”<br> — मोरोनाई 7:47</p></blockquote>',10)])])}const _=a(l,[["render",r]]);export{u as __pageData,_ as default};
