import{_ as e,c as i,o as t,a2 as r}from"./chunks/framework.C9y-vmhh.js";const p=JSON.parse('{"title":"InnerSight","description":"","frontmatter":{"layout":"home","hero":{"name":"InnerSight","text":"आंतरिक अन्वेषण और आत्म-जागरूकता के लिए स्थान","tagline":"व्यक्तिगत विकास के लिए ❤️ के साथ बनाया गया","image":{"src":"/logo.svg","alt":"InnerSight"},"actions":[{"theme":"brand","text":"शुरू करें","link":"/hi/about/"},{"theme":"alt","text":"GitHub पर देखें","link":"https://github.com/inner-clarity/InnerSight"}]}},"headers":[],"relativePath":"hi/index.md","filePath":"hi/index.md","lastUpdated":1761847046000}'),o={name:"hi/index.md"};function n(h,a,l,c,s,d){return t(),i("div",null,[...a[0]||(a[0]=[r('<h1 id="innersight" tabindex="-1">InnerSight <a class="header-anchor" href="#innersight" aria-label="Permalink to &quot;InnerSight&quot;">​</a></h1><h2 id="सभी-भगवान-के-बच्चों-के-समावेश-के-लिए-सूचित-समर्थन" tabindex="-1">सभी भगवान के बच्चों के समावेश के लिए सूचित समर्थन <a class="header-anchor" href="#सभी-भगवान-के-बच्चों-के-समावेश-के-लिए-सूचित-समर्थन" aria-label="Permalink to &quot;सभी भगवान के बच्चों के समावेश के लिए सूचित समर्थन&quot;">​</a></h2><p>सुसमाचार के संदर्भ में मानसिक स्वास्थ्य और न्यूरोलॉजिकल आवश्यकताओं को बेहतर समझने के लिए एक शैक्षिक संसाधन</p><hr><h3 id="उद्देश्य" tabindex="-1">उद्देश्य <a class="header-anchor" href="#उद्देश्य" aria-label="Permalink to &quot;उद्देश्य&quot;">​</a></h3><p>InnerSight एक शैक्षिक संसाधन है जो यीशु मसीह के लैटर-डे संतों के चर्च के नेताओं, परिवारों और सदस्यों को बेहतर समझने में सहायता करना चाहता है कि कैसे न्यूरोलॉजिकल अंतर और आघात प्रतिक्रियाएं भावनात्मक विनियमन के वैध रूपों की आवश्यकता हो सकती है। यह कार्य नैदानिक साक्ष्य, सुसमाचार सिद्धांतों और मसीह-समान प्रेम के साथ सेवा करने की साझा इच्छा पर आधारित है।</p><h3 id="आधार" tabindex="-1">आधार <a class="header-anchor" href="#आधार" aria-label="Permalink to &quot;आधार&quot;">​</a></h3><p>यह संसाधन नेताओं और परिवारों के सामान्य प्रश्नों का उत्तर देता है कि कैसे उन वफादार सदस्यों का समर्थन करें जिनकी मानसिक स्वास्थ्य या न्यूरोलॉजिकल आवश्यकताएं विशिष्ट अनुकूलन की आवश्यकता होती है। यह मान्यता प्राप्त नैदानिक मानकों (जैसे DSM-5), न्यूरोसाइंस अनुसंधान और पुनर्स्थापित सुसमाचार के सिद्धांतों पर आधारित है।</p><hr><h3 id="गवाही" tabindex="-1">गवाही <a class="header-anchor" href="#गवाही" aria-label="Permalink to &quot;गवाही&quot;">​</a></h3><blockquote><p><strong>चिंतनशील भाई</strong>: <br> &quot;मैं मेल्कीसेदेक पुजारीपन का धारक हूं, मैंने कई बुलाहटों में सेवा की है। मेरी न्यूरोलॉजिकल स्थिति संवेदी विनियमन के लिए विशिष्ट अनुकूलन की आवश्यकता है। वर्षों तक मैंने शर्म महसूस की, जब तक कि एक समझदार बिशप ने मुझसे नहीं कहा: &#39;भगवान के सामने आपकी गरिमा आपकी न्यूरोडायवर्सिटी पर निर्भर नहीं करती&#39;। उन शब्दों ने मेरा जीवन बदल दिया।&quot;</p></blockquote><blockquote><p><strong>वफादार सदस्य</strong>: <br> &quot;एक नेता के साथ एक साक्षात्कार में, जब मैंने व्यक्तिगत देखभाल वस्तुओं (PEPT) के उपयोग की आवश्यकता उठाई, तो इस नेता ने वैधता पर सवाल उठाया और स्पष्ट किया कि &#39;एक योग्य पुजारीपन धारक के लिए उसका उपयोग करना विरोधाभासी था या यह बुरा लगेगा, या यह शर्मनाक होगा&#39; यह समझे बिना कि यह एक सनक नहीं बल्कि एक न्यूरोलॉजिकल आवश्यकता थी। हालांकि मैंने खुद को गलत समझा महसूस किया, मैंने महसूस किया कि भगवान जानते हैं कि मेरे साथ क्या हो रहा है और वह मेरे दिल को समझते हैं।&quot;</p></blockquote><blockquote><p><strong>वापस आया मिशनरी</strong>: <br> &quot;मेरे मिशन के दौरान, मैं उन स्थितियों के संपर्क में आया जो दूसरों को असहज करती थीं, कभी-कभी मेरे पास टिक्स होते हैं, मैं यहां तक कि बेहोशी में मौखिक ध्वनियां भी निकालता हूं (टूरेट सिंड्रोम)। ऐसे नेता थे जिन्होंने मुझे &#39;अनादर या अश्रद्धा&#39; के लिए ध्यान दिलाया लेकिन मैंने इसे अनैच्छिक रूप से किया, मैं चाहता था कि वे इन न्यूरोलॉजिकल स्थितियों के बारे में सूचित हों। मैं आभारी हूं कि मेरे पास एक प्यार करने वाला और समझदार मिशन अध्यक्ष था, मेरे पास बहुत धैर्यवान साथी थे, उन्होंने मुझे महसूस कराया कि उद्धारकर्ता मेरी देखभाल कर रहे थे। मैंने अपना मिशन सम्मान के साथ समाप्त किया&quot;</p></blockquote><blockquote><p><strong>निःस्वार्थ मां</strong>: <br> &quot;कई लोगों ने मुझसे कहा &#39;मैं अपने बेटे को शांत क्यों नहीं रखती&#39; या कि &#39;मैं एक बुरी मां थी&#39; क्योंकि मैं अपने बेटे को नियंत्रित नहीं करती (मेरे बेटे को ऑटिज्म है), उन्होंने यहां तक कि चर्च में न जाने की संभावना का सुझाव दिया। वे समझते नहीं हैं कि मैं रोजाना क्या गुजरती हूं, फिर भी मुझे एक स्वर्गीय पिता से शक्ति मिलती है जो मुझसे प्यार करता है, एक प्यार करने वाले, वफादार और अच्छे पिता पति की शक्ति, और वादा कि अगर मैं वफादार रहूं तो &#39;प्रभु मुझे अपने कंधों पर ले जाएंगे&#39;। अगर दूसरे अधिक समझ रख सकते तो मुझे अपना बोझ थोड़ा हल्का लगता। लेकिन सबसे बढ़कर, मुझे पता है कि उद्धारकर्ता मुझे पूरी तरह समझते हैं, और यह मुझे शांति देता है।&quot;</p></blockquote><hr><h2 id="मुख्य-सामग्री" tabindex="-1">मुख्य सामग्री <a class="header-anchor" href="#मुख्य-सामग्री" aria-label="Permalink to &quot;मुख्य सामग्री&quot;">​</a></h2><h3 id="मनोवैज्ञानिक-नींव" tabindex="-1"><a href="/InnerSight/hi/analisis_psicologico_apropiado_v2">मनोवैज्ञानिक नींव</a> <a class="header-anchor" href="#मनोवैज्ञानिक-नींव" aria-label="Permalink to &quot;[मनोवैज्ञानिक नींव](/hi/analisis_psicologico_apropiado_v2)&quot;">​</a></h3><p>यह खंड एक नैदानिक दृष्टिकोण प्रस्तुत करता है कि लोग अपनी भावनाओं को कैसे नियंत्रित करते हैं, विशेष रूप से जब उन्होंने आघात का अनुभव किया है या न्यूरोलॉजिकल अंतर हैं। यह बताता है कि कब कुछ प्रतिक्रियाएं आवश्यक अनुकूली रणनीतियां हैं—विकार नहीं—वर्तमान चिकित्सा मानकों के अनुसार, न्यूरोसाइंस और अंतर्राष्ट्रीय नैदानिक दिशानिर्देशों सहित।</p><h3 id="भावनात्मक-विनियमन-आवश्यकताओं-की-नैदानिक-समझ" tabindex="-1"><a href="/InnerSight/hi/fundamento_cientifico_validacion">भावनात्मक विनियमन आवश्यकताओं की नैदानिक समझ</a> <a class="header-anchor" href="#भावनात्मक-विनियमन-आवश्यकताओं-की-नैदानिक-समझ" aria-label="Permalink to &quot;[भावनात्मक विनियमन आवश्यकताओं की नैदानिक समझ](/hi/fundamento_cientifico_validacion)&quot;">​</a></h3><p>साक्ष्य-आधारित मार्गदर्शिका यह पहचानने के लिए कि कब कुछ व्यवहार व्यक्तिगत प्राथमिकताओं के बजाय न्यूरोलॉजिकल आवश्यकताओं या आघात प्रतिक्रियाओं को दर्शाते हैं। नैदानिक मानदंड, उदाहरणात्मक उदाहरण और नेताओं और परिवारों के लिए मार्गदर्शन शामिल है जो समझ के साथ सेवा करना चाहते हैं।</p><h3 id="आवश्यक-विनियमन-रणनीतियों-के-परित्याग-की-मांग-के-प्रभाव" tabindex="-1"><a href="/InnerSight/hi/efectos_de_restricciones_coercitiva">आवश्यक विनियमन रणनीतियों के परित्याग की मांग के प्रभाव</a> <a class="header-anchor" href="#आवश्यक-विनियमन-रणनीतियों-के-परित्याग-की-मांग-के-प्रभाव" aria-label="Permalink to &quot;[आवश्यक विनियमन रणनीतियों के परित्याग की मांग के प्रभाव](/hi/efectos_de_restricciones_coercitiva)&quot;">​</a></h3><p>यह खंड न्यूरोसाइंस और नैदानिक अध्ययनों के आधार पर बताता है कि कैसे किसी से एक आवश्यक भावनात्मक विनियमन रणनीति का उपयोग बंद करने के लिए कहना—अच्छे इरादों के साथ भी—अतिरिक्त संकट, कार्यात्मक गिरावट और आध्यात्मिक दूरी का कारण बन सकता है।</p><h3 id="जब-प्रेम-अनुरूपता-पर-सशर्त-होता-है" tabindex="-1"><a href="/InnerSight/hi/chantaje_emocional">जब प्रेम अनुरूपता पर सशर्त होता है</a> <a class="header-anchor" href="#जब-प्रेम-अनुरूपता-पर-सशर्त-होता-है" aria-label="Permalink to &quot;[जब प्रेम अनुरूपता पर सशर्त होता है](/hi/chantaje_emocional)&quot;">​</a></h3><p>कुछ सदस्य रिपोर्ट करते हैं कि वे महसूस करते हैं कि उनकी संबद्धता या आध्यात्मिक मूल्य कुछ भावनात्मक विनियमन आवश्यकताओं को छोड़ने पर निर्भर करता है। यह खंड विश्लेषण करता है कि ये गतिशीलता—हालांकि अनजाने में—विश्वास, आत्म-सम्मान और रिश्तों को कैसे प्रभावित कर सकती हैं, और शर्तें लगाए बिना सेवा करने के सिद्धांत प्रदान करती हैं।</p><h3 id="भावनात्मक-विनियमन-की-प्रकृति" tabindex="-1"><a href="/InnerSight/hi/naturaleza_regulacion_emocional">भावनात्मक विनियमन की प्रकृति</a> <a class="header-anchor" href="#भावनात्मक-विनियमन-की-प्रकृति" aria-label="Permalink to &quot;[भावनात्मक विनियमन की प्रकृति](/hi/naturaleza_regulacion_emocional)&quot;">​</a></h3><p>कुछ लोग कुछ भावनात्मक विनियमन रणनीतियों को पहचान या यौन प्राथमिकता के मामलों के साथ भ्रमित करते हैं। यह खंड न्यूरोसाइंस के आधार पर एक स्पष्ट नैदानिक व्याख्या प्रदान करता है कि क्यों ये आवश्यकताएं यौन अभिविन्यास के बजाय मानसिक और न्यूरोलॉजिकल स्वास्थ्य से संबंधित हैं।</p><h3 id="भावनात्मक-वैवाहिक-सुरक्षा" tabindex="-1"><a href="/InnerSight/hi/seguridad_emocional_conyugal">भावनात्मक वैवाहिक सुरक्षा</a> <a class="header-anchor" href="#भावनात्मक-वैवाहिक-सुरक्षा" aria-label="Permalink to &quot;[भावनात्मक वैवाहिक सुरक्षा](/hi/seguridad_emocional_conyugal)&quot;">​</a></h3><p>यह खंड जांच करता है कि वैवाहिक अंतरंगता में बाध्यकारी प्रतिबंध कैसे पतियों की भावनात्मक सुरक्षा और मानसिक स्वास्थ्य को प्रभावित कर सकते हैं। यह वैवाहिक रिश्तों पर प्रभाव का विश्लेषण करता है जब ऐसे प्रतिबंध लगाए जाते हैं जो वैध भावनात्मक विनियमन में हस्तक्षेप करते हैं, सुसमाचार सिद्धांतों और वैवाहिक अंतरंगता की नैदानिक समझ के आधार पर दृष्टिकोण प्रदान करते हैं।</p><h3 id="आध्यात्मिक-गरिमा-और-मानसिक-स्वास्थ्य" tabindex="-1"><a href="/InnerSight/hi/sacerdocio_salud_mental_apropiado_v5">आध्यात्मिक गरिमा और मानसिक स्वास्थ्य</a> <a class="header-anchor" href="#आध्यात्मिक-गरिमा-और-मानसिक-स्वास्थ्य" aria-label="Permalink to &quot;[आध्यात्मिक गरिमा और मानसिक स्वास्थ्य](/hi/sacerdocio_salud_mental_apropiado_v5)&quot;">​</a></h3><p>यह खंड जांच करता है कि सुसमाचार शिक्षाएं—शास्त्रों, परिवार घोषणा और आधुनिक प्रकाशन सहित—कैसे पुष्टि करती हैं कि भगवान के सामने एक सदस्य की गरिमा उनकी मानसिक स्वास्थ्य या न्यूरोलॉजिकल स्थिति पर निर्भर नहीं करती, बल्कि उनके विश्वास, पश्चाताप और मसीह का अनुसरण करने की इच्छा पर निर्भर करती है।</p><h3 id="संस्थागत-प्रस्ताव" tabindex="-1"><a href="/InnerSight/hi/propuesta_v1_esp">संस्थागत प्रस्ताव</a> <a class="header-anchor" href="#संस्थागत-प्रस्ताव" aria-label="Permalink to &quot;[संस्थागत प्रस्ताव](/hi/propuesta_v1_esp)&quot;">​</a></h3><p>मानसिक स्वास्थ्य और न्यूरोलॉजिकल स्थितियों में संस्थागत समावेश के लिए प्रथम अध्यक्षता को प्रस्तुत व्यापक प्रस्ताव। वैज्ञानिक आधार, सिद्धांतिक सामंजस्य और नेताओं और समुदायों के लिए स्पष्ट मार्गदर्शन के साथ प्रस्तावित संस्थागत बयान शामिल है।</p><h3 id="नेताओं-के-लिए-व्यावहारिक-मार्गदर्शिका" tabindex="-1"><a href="/InnerSight/hi/guia_practica_lideres">नेताओं के लिए व्यावहारिक मार्गदर्शिका</a> <a class="header-anchor" href="#नेताओं-के-लिए-व्यावहारिक-मार्गदर्शिका" aria-label="Permalink to &quot;[नेताओं के लिए व्यावहारिक मार्गदर्शिका](/hi/guia_practica_lideres)&quot;">​</a></h3><p>नेताओं के लिए व्यावहारिक मार्गदर्शिका जो भावनात्मक विनियमन आवश्यकताओं वाले सदस्यों की सेवा करते समय सामान्य परिदृश्यों से निपटती है। वास्तविक स्थितियों में ज्ञान और करुणा के साथ कैसे प्रतिक्रिया दें इसके लिए विशिष्ट मार्गदर्शन शामिल है।</p><h3 id="नेताओं-के-लिए-अक्सर-पूछे-जाने-वाले-प्रश्न" tabindex="-1"><a href="/InnerSight/hi/preguntasfrecuentes">नेताओं के लिए अक्सर पूछे जाने वाले प्रश्न</a> <a class="header-anchor" href="#नेताओं-के-लिए-अक्सर-पूछे-जाने-वाले-प्रश्न" aria-label="Permalink to &quot;[नेताओं के लिए अक्सर पूछे जाने वाले प्रश्न](/hi/preguntasfrecuentes)&quot;">​</a></h3><p>नेताओं के लिए सामान्य प्रश्न और उत्तर जो मानसिक स्वास्थ्य और न्यूरोलॉजिकल आवश्यकताओं वाले सदस्यों का समर्थन करने के तरीके को बेहतर समझना चाहते हैं। वैध आवश्यकताओं, मंदिर की गरिमा, पारिवारिक गतिशीलता और उचित अनुकूलन को अलग करने के लिए व्यावहारिक मार्गदर्शन शामिल है।</p><h3 id="कठिन-पारिवारिक-बातचीत-के-लिए-मार्गदर्शिका" tabindex="-1"><a href="/InnerSight/hi/guia_conversacion">कठिन पारिवारिक बातचीत के लिए मार्गदर्शिका</a> <a class="header-anchor" href="#कठिन-पारिवारिक-बातचीत-के-लिए-मार्गदर्शिका" aria-label="Permalink to &quot;[कठिन पारिवारिक बातचीत के लिए मार्गदर्शिका](/hi/guia_conversacion)&quot;">​</a></h3><p>सदस्यों और परिवारों के लिए व्यावहारिक मार्गदर्शिका जो मानसिक स्वास्थ्य या न्यूरोलॉजिकल आवश्यकताओं के बारे में चुनौतीपूर्ण बातचीत का सामना करते हैं। आवश्यकताओं वाले लोगों और परिवार के सदस्यों दोनों के लिए विशिष्ट मार्गदर्शन शामिल है, संचार रणनीतियों, भावनात्मक प्रबंधन और पेशेवर समर्थन की तलाश के साथ।</p><hr><h2 id="उद्देश्य-1" tabindex="-1">उद्देश्य <a class="header-anchor" href="#उद्देश्य-1" aria-label="Permalink to &quot;उद्देश्य&quot;">​</a></h2><p>नेताओं, परिवारों और सदस्यों का समर्थन करना:</p><ul><li>मानसिक स्वास्थ्य और न्यूरोलॉजिकल आवश्यकताओं को बेहतर समझना</li><li>अधिक करुणा और ज्ञान के साथ सेवा करना</li><li>व्यक्तिगत परिस्थितियों के प्रति संवेदनशीलता के साथ सुसमाचार सिद्धांतों को लागू करना</li><li>अधिक समझ और पारस्परिक समर्थन के माध्यम से समुदाय को मजबूत करना</li></ul><hr><p><em>&quot;दया मसीह का शुद्ध प्रेम है, और यह हमेशा के लिए रहता है; और जो इसे अंतिम दिन पाएगा, उसे अच्छा होगा।&quot; - मोरोनी 7:47</em></p><div class="tip custom-block"><p class="custom-block-title">स्वागत है!</p><p>नोट: InnerSight एक स्वतंत्र शैक्षिक संसाधन है, नेताओं, परिवारों और पेशेवरों का समर्थन करने के लिए वफादार सदस्यों द्वारा बनाया गया। यह यीशु मसीह के लैटर-डे संतों के चर्च से संबद्ध या आधिकारिक रूप से समर्थित नहीं है।</p><p>हर आंतरिक यात्रा एक पहले कदम से शुरू होती है। आपकी यहां से शुरू होती है।</p></div>',45)])])}const b=e(o,[["render",n]]);export{p as __pageData,b as default};
